import React, { useState, useRef, useCallback, useEffect } from 'react';
import { Download, Upload, Plus, Settings, Trash2, Edit2, Link2, Save, X } from 'lucide-react';

const NodeGraphEditor = () => {
  // State management
  const [nodes, setNodes] = useState([]);
  const [connections, setConnections] = useState([]);
  const [nodeTypes, setNodeTypes] = useState({
    default: {
      name: 'Default',
      color: '#3b82f6',
      fields: [
        { name: 'id', type: 'string', required: true },
        { name: 'label', type: 'string', required: true },
        { name: 'description', type: 'text', required: false }
      ]
    }
  });
  const [linkTypes, setLinkTypes] = useState({
    default: {
      name: 'Default',
      color: '#6b7280',
      style: 'solid',
      fields: [
        { name: 'label', type: 'string', required: false },
        { name: 'weight', type: 'number', required: false }
      ]
    }
  });

  const [selectedNode, setSelectedNode] = useState(null);
  const [selectedConnection, setSelectedConnection] = useState(null);
  const [connecting, setConnecting] = useState(null);
  const [showSchemaEditor, setShowSchemaEditor] = useState(false);
  const [schemaEditType, setSchemaEditType] = useState('node');
  const [dragOffset, setDragOffset] = useState({ x: 0, y: 0 });
  const [nextNodeId, setNextNodeId] = useState(1);

  const canvasRef = useRef(null);
  const fileInputRef = useRef(null);
  const csvInputRef = useRef(null);

  // Helper functions
  const generateId = () => `node_${nextNodeId}`;

  const getNodeById = (id) => nodes.find(n => n.id === id);

  const addNode = (type = 'default', x = 100, y = 100) => {
    const nodeType = nodeTypes[type];
    const newNode = {
      id: generateId(),
      type,
      x,
      y,
      data: nodeType.fields.reduce((acc, field) => {
        if (field.required) {
          acc[field.name] = field.name === 'id' ? generateId() : 
                           field.name === 'label' ? `Node ${nextNodeId}` : '';
        }
        return acc;
      }, {})
    };

    setNodes(prev => [...prev, newNode]);
    setNextNodeId(prev => prev + 1);
    setSelectedNode(newNode);
  };

  const updateNode = (id, updates) => {
    setNodes(prev => prev.map(node => 
      node.id === id ? { ...node, ...updates } : node
    ));
    if (selectedNode?.id === id) {
      setSelectedNode(prev => ({ ...prev, ...updates }));
    }
  };

  const deleteNode = (id) => {
    setNodes(prev => prev.filter(n => n.id !== id));
    setConnections(prev => prev.filter(c => c.from !== id && c.to !== id));
    if (selectedNode?.id === id) {
      setSelectedNode(null);
    }
  };

  const addConnection = (from, to, type = 'default') => {
    const connection = {
      id: `conn_${Date.now()}`,
      from,
      to,
      type,
      data: {}
    };
    setConnections(prev => [...prev, connection]);
    return connection;
  };

  const deleteConnection = (id) => {
    setConnections(prev => prev.filter(c => c.id !== id));
    if (selectedConnection?.id === id) {
      setSelectedConnection(null);
    }
  };

  // Event handlers
  const handleCanvasClick = (e) => {
    if (e.target === canvasRef.current) {
      setSelectedNode(null);
      setSelectedConnection(null);
      setConnecting(null);
    }
  };

  const handleNodeClick = (node, e) => {
    e.stopPropagation();
    if (connecting) {
      if (connecting.id !== node.id) {
        addConnection(connecting.id, node.id);
      }
      setConnecting(null);
    } else {
      setSelectedNode(node);
      setSelectedConnection(null);
    }
  };

  const handleNodeMouseDown = (node, e) => {
    e.preventDefault();
    const rect = canvasRef.current.getBoundingClientRect();
    setDragOffset({
      x: e.clientX - rect.left - node.x,
      y: e.clientY - rect.top - node.y
    });

    const handleMouseMove = (e) => {
      const rect = canvasRef.current.getBoundingClientRect();
      updateNode(node.id, {
        x: e.clientX - rect.left - dragOffset.x,
        y: e.clientY - rect.top - dragOffset.y
      });
    };

    const handleMouseUp = () => {
      document.removeEventListener('mousemove', handleMouseMove);
      document.removeEventListener('mouseup', handleMouseUp);
    };

    document.addEventListener('mousemove', handleMouseMove);
    document.addEventListener('mouseup', handleMouseUp);
  };

  const startConnection = (node, e) => {
    e.stopPropagation();
    setConnecting(node);
  };

  // Export functions
  const exportAsCSV = () => {
    const nodeRows = nodes.map(node => ({
      type: 'node',
      id: node.id,
      nodeType: node.type,
      x: node.x,
      y: node.y,
      ...node.data
    }));

    const connectionRows = connections.map(conn => ({
      type: 'connection',
      id: conn.id,
      from: conn.from,
      to: conn.to,
      linkType: conn.type,
      ...conn.data
    }));

    const allRows = [...nodeRows, ...connectionRows];
    const headers = [...new Set(allRows.flatMap(Object.keys))];

    const csvContent = [
      headers.join(','),
      ...allRows.map(row => headers.map(h => `"${row[h] || ''}"`).join(','))
    ].join('\n');

    downloadFile(csvContent, 'diagram.csv', 'text/csv');
  };

  const exportAsMermaid = () => {
    const mermaid = [
      'graph TD',
      ...nodes.map(node => `    ${node.id}["${node.data.label || node.id}"]`),
      ...connections.map(conn => `    ${conn.from} --> ${conn.to}`)
    ].join('\n');

    downloadFile(mermaid, 'diagram.mmd', 'text/plain');
  };

  const exportAsSVG = () => {
    const svg = `
      <svg width="800" height="600" xmlns="http://www.w3.org/2000/svg">
        ${connections.map(conn => {
          const fromNode = getNodeById(conn.from);
          const toNode = getNodeById(conn.to);
          if (!fromNode || !toNode) return '';

          return `<line x1="${fromNode.x + 50}" y1="${fromNode.y + 25}" 
                        x2="${toNode.x + 50}" y2="${toNode.y + 25}" 
                        stroke="${linkTypes[conn.type]?.color || '#666'}" 
                        stroke-width="2" marker-end="url(#arrowhead)"/>`;
        }).join('')}

        ${nodes.map(node => `
          <rect x="${node.x}" y="${node.y}" width="100" height="50" 
                fill="${nodeTypes[node.type]?.color || '#3b82f6'}" 
                stroke="#333" stroke-width="1" rx="4"/>
          <text x="${node.x + 50}" y="${node.y + 30}" 
                text-anchor="middle" fill="white" font-family="Arial" font-size="12">
            ${node.data.label || node.id}
          </text>
        `).join('')}

        <defs>
          <marker id="arrowhead" markerWidth="10" markerHeight="7" 
                  refX="9" refY="3.5" orient="auto">
            <polygon points="0 0, 10 3.5, 0 7" fill="#666"/>
          </marker>
        </defs>
      </svg>`;

    downloadFile(svg, 'diagram.svg', 'image/svg+xml');
  };

  const downloadFile = (content, filename, type) => {
    const blob = new Blob([content], { type });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = filename;
    a.click();
    URL.revokeObjectURL(url);
  };

  const exportSchema = () => {
    const schema = { nodeTypes, linkTypes };
    downloadFile(JSON.stringify(schema, null, 2), 'schema.json', 'application/json');
  };

  // Import functions
  const importSchema = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => {
        try {
          const schema = JSON.parse(e.target.result);
          if (schema.nodeTypes) setNodeTypes(schema.nodeTypes);
          if (schema.linkTypes) setLinkTypes(schema.linkTypes);
        } catch (err) {
          alert('Invalid schema file');
        }
      };
      reader.readAsText(file);
    }
  };

  const importCSV = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => {
        try {
          const lines = e.target.result.split('\n');
          const headers = lines[0].split(',').map(h => h.replace(/"/g, ''));

          const newNodes = [];
          const newConnections = [];

          lines.slice(1).forEach(line => {
            if (!line.trim()) return;
            const values = line.split(',').map(v => v.replace(/"/g, ''));
            const row = headers.reduce((obj, header, i) => {
              obj[header] = values[i] || '';
              return obj;
            }, {});

            if (row.type === 'node') {
              const { type: _, nodeType, x, y, id, ...data } = row;
              newNodes.push({
                id: id || `imported_${Date.now()}_${Math.random()}`,
                type: nodeType || 'default',
                x: parseFloat(x) || 100,
                y: parseFloat(y) || 100,
                data
              });
            } else if (row.type === 'connection') {
              const { type: _, linkType, from, to, id, ...data } = row;
              newConnections.push({
                id: id || `conn_${Date.now()}_${Math.random()}`,
                from,
                to,
                type: linkType || 'default',
                data
              });
            }
          });

          setNodes(newNodes);
          setConnections(newConnections);
        } catch (err) {
          alert('Invalid CSV file');
        }
      };
      reader.readAsText(file);
    }
  };

  return (
    <div className="w-full h-screen flex bg-gray-50">
      {/* Sidebar */}
      <div className="w-80 bg-white border-r border-gray-200 p-4 overflow-y-auto">
        <div className="space-y-4">
          {/* Tools */}
          <div className="border-b pb-4">
            <h3 className="font-semibold mb-3">Tools</h3>
            <div className="grid grid-cols-2 gap-2">
              <button 
                onClick={() => addNode()}
                className="flex items-center gap-2 p-2 bg-blue-500 text-white rounded hover:bg-blue-600"
              >
                <Plus size={16} /> Add Node
              </button>
              <button 
                onClick={() => setShowSchemaEditor(true)}
                className="flex items-center gap-2 p-2 bg-gray-500 text-white rounded hover:bg-gray-600"
              >
                <Settings size={16} /> Schema
              </button>
            </div>
          </div>

          {/* Export/Import */}
          <div className="border-b pb-4">
            <h3 className="font-semibold mb-3">Export</h3>
            <div className="space-y-2">
              <button onClick={exportAsCSV} className="w-full flex items-center gap-2 p-2 bg-green-500 text-white rounded hover:bg-green-600">
                <Download size={16} /> Export CSV
              </button>
              <button onClick={exportAsMermaid} className="w-full flex items-center gap-2 p-2 bg-purple-500 text-white rounded hover:bg-purple-600">
                <Download size={16} /> Export Mermaid
              </button>
              <button onClick={exportAsSVG} className="w-full flex items-center gap-2 p-2 bg-orange-500 text-white rounded hover:bg-orange-600">
                <Download size={16} /> Export SVG
              </button>
              <button onClick={exportSchema} className="w-full flex items-center gap-2 p-2 bg-indigo-500 text-white rounded hover:bg-indigo-600">
                <Download size={16} /> Export Schema
              </button>
            </div>

            <h3 className="font-semibold mt-4 mb-3">Import</h3>
            <div className="space-y-2">
              <button 
                onClick={() => csvInputRef.current?.click()}
                className="w-full flex items-center gap-2 p-2 bg-blue-500 text-white rounded hover:bg-blue-600"
              >
                <Upload size={16} /> Import CSV
              </button>
              <button 
                onClick={() => fileInputRef.current?.click()}
                className="w-full flex items-center gap-2 p-2 bg-gray-500 text-white rounded hover:bg-gray-600"
              >
                <Upload size={16} /> Import Schema
              </button>
            </div>
          </div>

          {/* Node Properties */}
          {selectedNode && (
            <div className="border-b pb-4">
              <h3 className="font-semibold mb-3">Node Properties</h3>
              <div className="space-y-2">
                <div>
                  <label className="block text-sm font-medium">Type</label>
                  <select 
                    value={selectedNode.type}
                    onChange={(e) => updateNode(selectedNode.id, { type: e.target.value })}
                    className="w-full p-1 border rounded"
                  >
                    {Object.keys(nodeTypes).map(type => (
                      <option key={type} value={type}>{nodeTypes[type].name}</option>
                    ))}
                  </select>
                </div>
                {nodeTypes[selectedNode.type]?.fields.map(field => (
                  <div key={field.name}>
                    <label className="block text-sm font-medium">{field.name}</label>
                    {field.type === 'text' ? (
                      <textarea
                        value={selectedNode.data[field.name] || ''}
                        onChange={(e) => updateNode(selectedNode.id, {
                          data: { ...selectedNode.data, [field.name]: e.target.value }
                        })}
                        className="w-full p-1 border rounded"
                        rows="3"
                      />
                    ) : (
                      <input
                        type={field.type === 'number' ? 'number' : 'text'}
                        value={selectedNode.data[field.name] || ''}
                        onChange={(e) => updateNode(selectedNode.id, {
                          data: { ...selectedNode.data, [field.name]: e.target.value }
                        })}
                        className="w-full p-1 border rounded"
                      />
                    )}
                  </div>
                ))}
                <button
                  onClick={() => deleteNode(selectedNode.id)}
                  className="w-full flex items-center justify-center gap-2 p-2 bg-red-500 text-white rounded hover:bg-red-600"
                >
                  <Trash2 size={16} /> Delete Node
                </button>
              </div>
            </div>
          )}

          {/* Instructions */}
          <div className="text-sm text-gray-600">
            <h3 className="font-semibold mb-2">How to use:</h3>
            <ul className="space-y-1">
              <li>• Click "Add Node" to create nodes</li>
              <li>• Drag nodes to move them</li>
              <li>• Click the link icon on a node to start connecting</li>
              <li>• Click another node to complete the connection</li>
              <li>• Click on nodes to edit their properties</li>
              <li>• Use Schema to define node and link types</li>
            </ul>
          </div>
        </div>
      </div>

      {/* Canvas */}
      <div className="flex-1 relative overflow-hidden">
        <svg
          ref={canvasRef}
          className="w-full h-full cursor-crosshair"
          onClick={handleCanvasClick}
        >
          {/* Render connections */}
          {connections.map(connection => {
            const fromNode = getNodeById(connection.from);
            const toNode = getNodeById(connection.to);
            if (!fromNode || !toNode) return null;

            const linkType = linkTypes[connection.type] || linkTypes.default;

            return (
              <g key={connection.id}>
                <line
                  x1={fromNode.x + 60}
                  y1={fromNode.y + 30}
                  x2={toNode.x + 60}
                  y2={toNode.y + 30}
                  stroke={linkType.color}
                  strokeWidth="2"
                  markerEnd="url(#arrowhead)"
                  className="cursor-pointer hover:stroke-width-3"
                  onClick={(e) => {
                    e.stopPropagation();
                    setSelectedConnection(connection);
                    setSelectedNode(null);
                  }}
                />
                {connection.data.label && (
                  <text
                    x={(fromNode.x + toNode.x) / 2 + 60}
                    y={(fromNode.y + toNode.y) / 2 + 25}
                    textAnchor="middle"
                    className="fill-gray-600 text-sm pointer-events-none"
                  >
                    {connection.data.label}
                  </text>
                )}
              </g>
            );
          })}

          {/* Arrow marker */}
          <defs>
            <marker
              id="arrowhead"
              markerWidth="10"
              markerHeight="7"
              refX="9"
              refY="3.5"
              orient="auto"
            >
              <polygon points="0 0, 10 3.5, 0 7" fill="#666" />
            </marker>
          </defs>
        </svg>

        {/* Render nodes */}
        {nodes.map(node => {
          const nodeType = nodeTypes[node.type] || nodeTypes.default;
          const isSelected = selectedNode?.id === node.id;

          return (
            <div
              key={node.id}
              className={`absolute select-none cursor-pointer border-2 rounded-lg shadow-lg ${
                isSelected ? 'border-yellow-400' : 'border-gray-300'
              }`}
              style={{
                left: node.x,
                top: node.y,
                backgroundColor: nodeType.color,
                color: 'white',
                width: '120px',
                height: '60px'
              }}
              onClick={(e) => handleNodeClick(node, e)}
              onMouseDown={(e) => handleNodeMouseDown(node, e)}
            >
              <div className="p-2 h-full flex flex-col justify-between">
                <div className="text-sm font-medium truncate">
                  {node.data.label || node.id}
                </div>
                <div className="flex justify-between items-end">
                  <span className="text-xs opacity-75">{nodeType.name}</span>
                  <button
                    onClick={(e) => startConnection(node, e)}
                    className="p-1 hover:bg-black hover:bg-opacity-20 rounded"
                    title="Create connection"
                  >
                    <Link2 size={12} />
                  </button>
                </div>
              </div>
            </div>
          );
        })}

        {/* Connection preview */}
        {connecting && (
          <div className="absolute inset-0 pointer-events-none">
            <div 
              className="absolute w-4 h-4 bg-yellow-400 rounded-full animate-pulse"
              style={{
                left: connecting.x + 56,
                top: connecting.y + 26
              }}
            />
          </div>
        )}
      </div>

      {/* Schema Editor Modal */}
      {showSchemaEditor && (
        <SchemaEditor
          nodeTypes={nodeTypes}
          linkTypes={linkTypes}
          onUpdateNodeTypes={setNodeTypes}
          onUpdateLinkTypes={setLinkTypes}
          onClose={() => setShowSchemaEditor(false)}
          editType={schemaEditType}
          onEditTypeChange={setSchemaEditType}
        />
      )}

      {/* Hidden file inputs */}
      <input
        ref={fileInputRef}
        type="file"
        accept=".json"
        style={{ display: 'none' }}
        onChange={importSchema}
      />
      <input
        ref={csvInputRef}
        type="file"
        accept=".csv"
        style={{ display: 'none' }}
        onChange={importCSV}
      />
    </div>
  );
};

// Schema Editor Component
const SchemaEditor = ({ nodeTypes, linkTypes, onUpdateNodeTypes, onUpdateLinkTypes, onClose, editType, onEditTypeChange }) => {
  const [editingType, setEditingType] = useState(null);
  const [tempType, setTempType] = useState(null);

  const types = editType === 'node' ? nodeTypes : linkTypes;
  const updateTypes = editType === 'node' ? onUpdateNodeTypes : onUpdateLinkTypes;

  const startEditing = (typeKey) => {
    const type = types[typeKey];
    setTempType({ key: typeKey, ...type });
    setEditingType(typeKey);
  };

  const saveType = () => {
    if (tempType) {
      const { key, ...typeData } = tempType;
      const newTypes = { ...types };

      if (key !== editingType) {
        delete newTypes[editingType];
      }

      newTypes[key] = typeData;
      updateTypes(newTypes);
      setEditingType(null);
      setTempType(null);
    }
  };

  const addNewType = () => {
    const newKey = `${editType}_${Date.now()}`;
    const newType = {
      key: newKey,
      name: 'New Type',
      color: '#3b82f6',
      fields: [
        { name: 'label', type: 'string', required: true }
      ]
    };

    if (editType === 'link') {
      newType.style = 'solid';
    }

    setTempType(newType);
    setEditingType(newKey);
  };

  const deleteType = (typeKey) => {
    if (typeKey === 'default') return; // Can't delete default
    const newTypes = { ...types };
    delete newTypes[typeKey];
    updateTypes(newTypes);
  };

  const addField = () => {
    if (tempType) {
      setTempType({
        ...tempType,
        fields: [...tempType.fields, { name: '', type: 'string', required: false }]
      });
    }
  };

  const updateField = (index, field) => {
    if (tempType) {
      const newFields = [...tempType.fields];
      newFields[index] = field;
      setTempType({ ...tempType, fields: newFields });
    }
  };

  const removeField = (index) => {
    if (tempType && tempType.fields.length > 1) {
      const newFields = tempType.fields.filter((_, i) => i !== index);
      setTempType({ ...tempType, fields: newFields });
    }
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
      <div className="bg-white rounded-lg p-6 w-2/3 h-2/3 overflow-y-auto">
        <div className="flex justify-between items-center mb-4">
          <h2 className="text-xl font-semibold">Schema Editor</h2>
          <button onClick={onClose} className="p-1 hover:bg-gray-100 rounded">
            <X size={20} />
          </button>
        </div>

        <div className="flex gap-4 mb-4">
          <button
            onClick={() => onEditTypeChange('node')}
            className={`px-4 py-2 rounded ${editType === 'node' ? 'bg-blue-500 text-white' : 'bg-gray-200'}`}
          >
            Node Types
          </button>
          <button
            onClick={() => onEditTypeChange('link')}
            className={`px-4 py-2 rounded ${editType === 'link' ? 'bg-blue-500 text-white' : 'bg-gray-200'}`}
          >
            Link Types
          </button>
        </div>

        <div className="flex gap-6">
          {/* Type List */}
          <div className="w-1/3">
            <div className="flex justify-between items-center mb-2">
              <h3 className="font-medium">{editType === 'node' ? 'Node' : 'Link'} Types</h3>
              <button
                onClick={addNewType}
                className="p-1 bg-green-500 text-white rounded hover:bg-green-600"
              >
                <Plus size={16} />
              </button>
            </div>
            <div className="space-y-2">
              {Object.keys(types).map(typeKey => (
                <div key={typeKey} className="flex items-center justify-between p-2 border rounded">
                  <div className="flex items-center gap-2">
                    <div 
                      className="w-4 h-4 rounded"
                      style={{ backgroundColor: types[typeKey].color }}
                    />
                    <span>{types[typeKey].name}</span>
                  </div>
                  <div className="flex gap-1">
                    <button
                      onClick={() => startEditing(typeKey)}
                      className="p-1 hover:bg-gray-100 rounded"
                    >
                      <Edit2 size={14} />
                    </button>
                    {typeKey !== 'default' && (
                      <button
                        onClick={() => deleteType(typeKey)}
                        className="p-1 hover:bg-gray-100 rounded text-red-500"
                      >
                        <Trash2 size={14} />
                      </button>
                    )}
                  </div>
                </div>
              ))}
            </div>
          </div>

          {/* Type Editor */}
          <div className="flex-1">
            {tempType && (
              <div className="space-y-4">
                <div className="flex justify-between items-center">
                  <h3 className="font-medium">Edit {editType === 'node' ? 'Node' : 'Link'} Type</h3>
                  <div className="flex gap-2">
                    <button
                      onClick={saveType}
                      className="px-3 py-1 bg-blue-500 text-white rounded hover:bg-blue-600"
                    >
                      <Save size={16} />
                    </button>
                    <button
                      onClick={() => { setEditingType(null); setTempType(null); }}
                      className="px-3 py-1 bg-gray-500 text-white rounded hover:bg-gray-600"
                    >
                      Cancel
                    </button>
                  </div>
                </div>

                <div className="grid grid-cols-2 gap-4">
                  <div>
                    <label className="block text-sm font-medium">Key</label>
                    <input
                      type="text"
                      value={tempType.key}
                      onChange={(e) => setTempType({ ...tempType, key: e.target.value })}
                      className="w-full p-2 border rounded"
                    />
                  </div>
                  <div>
                    <label className="block text-sm font-medium">Name</label>
                    <input
                      type="text"
                      value={tempType.name}
                      onChange={(e) => setTempType({ ...tempType, name: e.target.value })}
                      className="w-full p-2 border rounded"
                    />
                  </div>
                  <div>
                    <label className="block text-sm font-medium">Color</label>
                    <input
                      type="color"
                      value={tempType.color}
                      onChange={(e) => setTempType({ ...tempType, color: e.target.value })}
                      className="w-full p-2 border rounded"
                    />
                  </div>
                  {editType === 'link' && (
                    <div>
                      <label className="block text-sm font-medium">Style</label>
                      <select
                        value={tempType.style || 'solid'}
                        onChange={(e) => setTempType({ ...tempType, style: e.target.value })}
                        className="w-full p-2 border rounded"
                      >
                        <option value="solid">Solid</option>
                        <option value="dashed">Dashed</option>
                        <option value="dotted">Dotted</option>
                      </select>
                    </div>
                  )}
                </div>

                {/* Fields */}
                <div>
                  <div className="flex justify-between items-center mb-2">
                    <label className="block text-sm font-medium">Fields</label>
                    <button
                      onClick={addField}
                      className="px-2 py-1 bg-green-500 text-white rounded hover:bg-green-600"
                    >
                      Add Field
                    </button>
                  </div>
                  <div className="space-y-2">
                    {tempType.fields.map((field, index) => (
                      <div key={index} className="grid grid-cols-4 gap-2 p-2 border rounded">
                        <input
                          type="text"
                          placeholder="Field name"
                          value={field.name}
                          onChange={(e) => updateField(index, { ...field, name: e.target.value })}
                          className="p-1 border rounded"
                        />
                        <select
                          value={field.type}
                          onChange={(e) => updateField(index, { ...field, type: e.target.value })}
                          className="p-1 border rounded"
                        >
                          <option value="string">String</option>
                          <option value="number">Number</option>
                          <option value="text">Text</option>
                          <option value="boolean">Boolean</option>
                        </select>
                        <label className="flex items-center">
                          <input
                            type="checkbox"
                            checked={field.required}
                            onChange={(e) => updateField(index, { ...field, required: e.target.checked })}
                            className="mr-1"
                          />
                          Required
                        </label>
                        <button
                          onClick={() => removeField(index)}
                          className="p-1 text-red-500 hover:bg-red-50 rounded"
                          disabled={tempType.fields.length <= 1}
                        >
                          <Trash2 size={14} />
                        </button>
                      </div>
                    ))}
                  </div>
                </div>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

