import React, { useState, useRef } from 'react';
import { Upload, FileText, Globe, Download, Play, Pause, AlertCircle, CheckCircle, Eye, EyeOff } from 'lucide-react';

const URLMetadataAnalyzer = () => {
  const [inputData, setInputData] = useState([]);
  const [results, setResults] = useState([]);
  const [isProcessing, setIsProcessing] = useState(false);
  const [processingIndex, setProcessingIndex] = useState(-1);
  const [inputMethod, setInputMethod] = useState('manual');
  const [textInput, setTextInput] = useState('');
  const [showApiKey, setShowApiKey] = useState(false);
  const fileInputRef = useRef(null);

  // Sample data structure
  const sampleData = [
    {
      url: 'https://www.anthropic.com',
      title: 'Anthropic Homepage',
      summary: 'AI safety company focused on building safe, beneficial AI systems'
    },
    {
      url: 'https://docs.anthropic.com',
      title: 'Anthropic Documentation',
      summary: 'Technical documentation for Claude API and AI safety research'
    }
  ];

  const validateUrl = (urlString) => {
    try {
      const url = new URL(urlString);
      return url.protocol === 'http:' || url.protocol === 'https:';
    } catch {
      return false;
    }
  };

  const extractMetadata = async (url) => {
    try {
      const response = await fetch("https://api.anthropic.com/v1/messages", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          model: "claude-sonnet-4-20250514",
          max_tokens: 500,
          messages: [
            {
              role: "user",
              content: `Please fetch and analyze this URL: ${url}

              Extract the following metadata and return as JSON:
              {
                "title": "page title",
                "description": "meta description or first paragraph",
                "domain": "domain name",
                "wordCount": "estimated word count",
                "contentType": "article/homepage/blog/etc",
                "language": "detected language"
              }

              Only return valid JSON, no other text.`
            }
          ]
        })
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const data = await response.json();
      const content = data.content[0].text;

      try {
        const cleanedContent = content.replace(/```json\n?/g, "").replace(/```\n?/g, "").trim();
        return JSON.parse(cleanedContent);
      } catch (parseError) {
        return {
          title: "Unable to extract",
          description: "Could not parse metadata",
          domain: new URL(url).hostname,
          wordCount: "Unknown",
          contentType: "Unknown",
          language: "Unknown"
        };
      }
    } catch (error) {
      console.error('Error extracting metadata:', error);
      return {
        title: "Error",
        description: error.message,
        domain: new URL(url).hostname,
        wordCount: "Error",
        contentType: "Error",
        language: "Error"
      };
    }
  };

  const generateSummary = async (url, metadata) => {
    try {
      const response = await fetch("https://api.anthropic.com/v1/messages", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          model: "claude-sonnet-4-20250514",
          max_tokens: 300,
          messages: [
            {
              role: "user",
              content: `Based on the URL ${url} and this metadata: ${JSON.stringify(metadata)}, 
              generate a concise 2-3 sentence summary of what this webpage is about. 
              Focus on the main purpose, content type, and target audience.`
            }
          ]
        })
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const data = await response.json();
      return data.content[0].text.trim();
    } catch (error) {
      console.error('Error generating summary:', error);
      return "Unable to generate summary due to API error.";
    }
  };

  const calculateSimilarity = async (summary1, summary2) => {
    try {
      const response = await fetch("https://api.anthropic.com/v1/messages", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          model: "claude-sonnet-4-20250514",
          max_tokens: 100,
          messages: [
            {
              role: "user",
              content: `Compare these two summaries and give a similarity score from 0-100:

              Summary 1: "${summary1}"
              Summary 2: "${summary2}"

              Return only a number between 0-100 representing similarity percentage.`
            }
          ]
        })
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const data = await response.json();
      const score = parseInt(data.content[0].text.trim());
      return isNaN(score) ? 0 : Math.min(100, Math.max(0, score));
    } catch (error) {
      console.error('Error calculating similarity:', error);
      return 0;
    }
  };

  const processUrls = async () => {
    if (inputData.length === 0) {
      alert('Please add some URLs to process');
      return;
    }

    setIsProcessing(true);
    setResults([]);

    const newResults = [];

    for (let i = 0; i < inputData.length; i++) {
      setProcessingIndex(i);
      const item = inputData[i];

      const result = {
        originalData: item,
        isValid: validateUrl(item.url),
        metadata: null,
        generatedSummary: '',
        similarityScore: null,
        error: null
      };

      if (result.isValid) {
        try {
          // Extract metadata
          result.metadata = await extractMetadata(item.url);

          // Generate summary
          result.generatedSummary = await generateSummary(item.url, result.metadata);

          // Calculate similarity if original summary provided
          if (item.summary && item.summary.trim()) {
            result.similarityScore = await calculateSimilarity(item.summary, result.generatedSummary);
          }
        } catch (error) {
          result.error = error.message;
        }
      } else {
        result.error = 'Invalid URL format';
      }

      newResults.push(result);
      setResults([...newResults]);

      // Small delay to prevent overwhelming the API
      if (i < inputData.length - 1) {
        await new Promise(resolve => setTimeout(resolve, 1000));
      }
    }

    setIsProcessing(false);
    setProcessingIndex(-1);
  };

  const handleFileUpload = async (event) => {
    const file = event.target.files[0];
    if (!file) return;

    const text = await file.text();

    try {
      if (file.name.endsWith('.json')) {
        const jsonData = JSON.parse(text);
        const urls = Array.isArray(jsonData) ? jsonData : [jsonData];
        setInputData(urls.map(item => ({
          url: item.url || item.link || item.href || '',
          title: item.title || '',
          summary: item.summary || item.description || ''
        })));
      } else if (file.name.endsWith('.csv')) {
        const lines = text.split('\n').filter(line => line.trim());
        const headers = lines[0].split(',').map(h => h.trim().toLowerCase());
        const data = [];

        for (let i = 1; i < lines.length; i++) {
          const values = lines[i].split(',');
          const item = {};
          headers.forEach((header, index) => {
            const value = values[index]?.trim() || '';
            if (header.includes('url') || header.includes('link')) {
              item.url = value;
            } else if (header.includes('title') || header.includes('name')) {
              item.title = value;
            } else if (header.includes('summary') || header.includes('description')) {
              item.summary = value;
            }
          });
          if (item.url) data.push(item);
        }
        setInputData(data);
      } else {
        // Treat as plain text with URLs
        const urls = text.split('\n')
          .filter(line => line.trim())
          .map(url => ({ url: url.trim(), title: '', summary: '' }));
        setInputData(urls);
      }
    } catch (error) {
      alert('Error parsing file: ' + error.message);
    }
  };

  const handleTextInput = () => {
    const lines = textInput.split('\n').filter(line => line.trim());
    const urls = lines.map(line => {
      const parts = line.split('|').map(p => p.trim());
      return {
        url: parts[0] || '',
        title: parts[1] || '',
        summary: parts[2] || ''
      };
    });
    setInputData(urls);
    setTextInput('');
  };

  const exportResults = (format) => {
    if (results.length === 0) {
      alert('No results to export');
      return;
    }

    let content, filename, mimeType;

    if (format === 'json') {
      content = JSON.stringify(results, null, 2);
      filename = 'url-analysis-results.json';
      mimeType = 'application/json';
    } else if (format === 'csv') {
      const headers = ['URL', 'Original Title', 'Original Summary', 'Is Valid', 'Extracted Title', 'Description', 'Domain', 'Content Type', 'Generated Summary', 'Similarity Score', 'Error'];
      const rows = results.map(result => [
        result.originalData.url,
        result.originalData.title,
        result.originalData.summary,
        result.isValid,
        result.metadata?.title || '',
        result.metadata?.description || '',
        result.metadata?.domain || '',
        result.metadata?.contentType || '',
        result.generatedSummary,
        result.similarityScore || '',
        result.error || ''
      ]);
      content = [headers, ...rows].map(row => row.map(cell => `"${cell}"`).join(',')).join('\n');
      filename = 'url-analysis-results.csv';
      mimeType = 'text/csv';
    }

    const blob = new Blob([content], { type: mimeType });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = filename;
    a.click();
    URL.revokeObjectURL(url);
  };

  const loadSampleData = () => {
    setInputData(sampleData);
  };

  return (
    <div className="max-w-6xl mx-auto p-6 bg-gray-50 min-h-screen">
      <div className="bg-white rounded-lg shadow-lg p-6">
        <div className="text-center mb-8">
          <h1 className="text-3xl font-bold text-gray-800 mb-2">URL Metadata Analyzer</h1>
          <p className="text-gray-600">Validate URLs, extract metadata, generate summaries, and compare content</p>
        </div>

        {/* Input Section */}
        <div className="mb-8">
          <h2 className="text-xl font-semibold mb-4 flex items-center">
            <Upload className="mr-2" size={20} />
            Input Data
          </h2>

          <div className="mb-4">
            <div className="flex gap-4 mb-4">
              <button
                onClick={() => setInputMethod('manual')}
                className={`px-4 py-2 rounded ${inputMethod === 'manual' ? 'bg-blue-500 text-white' : 'bg-gray-200'}`}
              >
                Manual Entry
              </button>
              <button
                onClick={() => setInputMethod('file')}
                className={`px-4 py-2 rounded ${inputMethod === 'file' ? 'bg-blue-500 text-white' : 'bg-gray-200'}`}
              >
                File Upload
              </button>
              <button
                onClick={loadSampleData}
                className="px-4 py-2 bg-green-500 text-white rounded hover:bg-green-600"
              >
                Load Sample Data
              </button>
            </div>

            {inputMethod === 'manual' && (
              <div>
                <textarea
                  value={textInput}
                  onChange={(e) => setTextInput(e.target.value)}
                  placeholder="Enter URLs, one per line. Use | to separate URL|Title|Summary&#10;Example:&#10;https://example.com|Example Site|A sample website&#10;https://another.com|Another Site"
                  className="w-full h-32 p-3 border rounded resize-none"
                />
                <button
                  onClick={handleTextInput}
                  className="mt-2 px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600"
                >
                  Add URLs
                </button>
              </div>
            )}

            {inputMethod === 'file' && (
              <div>
                <input
                  ref={fileInputRef}
                  type="file"
                  accept=".csv,.json,.txt"
                  onChange={handleFileUpload}
                  className="hidden"
                />
                <button
                  onClick={() => fileInputRef.current?.click()}
                  className="px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600 flex items-center"
                >
                  <FileText className="mr-2" size={16} />
                  Choose File (CSV, JSON, TXT)
                </button>
                <p className="text-sm text-gray-500 mt-2">
                  CSV: headers should include url/link, title, summary/description<br/>
                  JSON: array of objects with url, title, summary fields<br/>
                  TXT: one URL per line
                </p>
              </div>
            )}
          </div>

          {/* Input Data Preview */}
          {inputData.length > 0 && (
            <div className="mt-4">
              <h3 className="font-semibold mb-2">Input Data ({inputData.length} URLs)</h3>
              <div className="max-h-40 overflow-y-auto border rounded p-2 bg-gray-50">
                {inputData.map((item, index) => (
                  <div key={index} className="text-sm mb-1">
                    <span className="font-mono text-blue-600">{item.url}</span>
                    {item.title && <span className="ml-2 text-gray-600">• {item.title}</span>}
                  </div>
                ))}
              </div>
            </div>
          )}
        </div>

        {/* Processing Section */}
        <div className="mb-8">
          <div className="flex items-center justify-between">
            <h2 className="text-xl font-semibold flex items-center">
              <Globe className="mr-2" size={20} />
              Process URLs
            </h2>
            <button
              onClick={processUrls}
              disabled={isProcessing || inputData.length === 0}
              className="px-6 py-2 bg-green-500 text-white rounded hover:bg-green-600 disabled:opacity-50 flex items-center"
            >
              {isProcessing ? (
                <>
                  <Pause className="mr-2" size={16} />
                  Processing...
                </>
              ) : (
                <>
                  <Play className="mr-2" size={16} />
                  Start Analysis
                </>
              )}
            </button>
          </div>

          {isProcessing && (
            <div className="mt-4">
              <div className="bg-blue-50 p-4 rounded">
                <p className="text-sm text-blue-700">
                  Processing URL {processingIndex + 1} of {inputData.length}...
                </p>
                <div className="w-full bg-blue-200 rounded-full h-2 mt-2">
                  <div 
                    className="bg-blue-500 h-2 rounded-full transition-all duration-300"
                    style={{ width: `${((processingIndex + 1) / inputData.length) * 100}%` }}
                  ></div>
                </div>
              </div>
            </div>
          )}
        </div>

        {/* Results Section */}
        {results.length > 0 && (
          <div className="mb-8">
            <div className="flex items-center justify-between mb-4">
              <h2 className="text-xl font-semibold">Results ({results.length})</h2>
              <div className="flex gap-2">
                <button
                  onClick={() => exportResults('json')}
                  className="px-4 py-2 bg-purple-500 text-white rounded hover:bg-purple-600 flex items-center"
                >
                  <Download className="mr-2" size={16} />
                  Export JSON
                </button>
                <button
                  onClick={() => exportResults('csv')}
                  className="px-4 py-2 bg-purple-500 text-white rounded hover:bg-purple-600 flex items-center"
                >
                  <Download className="mr-2" size={16} />
                  Export CSV
                </button>
              </div>
            </div>

            <div className="space-y-4 max-h-96 overflow-y-auto">
              {results.map((result, index) => (
                <div key={index} className="border rounded p-4 bg-white">
                  <div className="flex items-start justify-between mb-2">
                    <div className="flex-1">
                      <div className="flex items-center mb-1">
                        {result.isValid ? (
                          <CheckCircle className="text-green-500 mr-2" size={16} />
                        ) : (
                          <AlertCircle className="text-red-500 mr-2" size={16} />
                        )}
                        <span className="font-mono text-sm text-blue-600 break-all">
                          {result.originalData.url}
                        </span>
                      </div>
                      {result.originalData.title && (
                        <p className="text-sm text-gray-600 mb-1">
                          <strong>Original Title:</strong> {result.originalData.title}
                        </p>
                      )}
                    </div>
                  </div>

                  {result.error ? (
                    <div className="text-red-600 text-sm">
                      <strong>Error:</strong> {result.error}
                    </div>
                  ) : result.metadata && (
                    <div className="grid md:grid-cols-2 gap-4 text-sm">
                      <div>
                        <h4 className="font-semibold mb-2">Extracted Metadata</h4>
                        <p><strong>Title:</strong> {result.metadata.title}</p>
                        <p><strong>Domain:</strong> {result.metadata.domain}</p>
                        <p><strong>Content Type:</strong> {result.metadata.contentType}</p>
                        <p><strong>Language:</strong> {result.metadata.language}</p>
                        {result.metadata.description && (
                          <p><strong>Description:</strong> {result.metadata.description.substring(0, 100)}...</p>
                        )}
                      </div>
                      <div>
                        <h4 className="font-semibold mb-2">Generated Summary</h4>
                        <p className="text-gray-700 mb-2">{result.generatedSummary}</p>

                        {result.originalData.summary && result.similarityScore !== null && (
                          <div className="mt-2">
                            <h5 className="font-semibold">Summary Comparison</h5>
                            <p><strong>Original:</strong> {result.originalData.summary}</p>
                            <div className="flex items-center mt-1">
                              <span className="mr-2">Similarity Score:</span>
                              <div className="flex-1 bg-gray-200 rounded-full h-2 mr-2">
                                <div 
                                  className={`h-2 rounded-full ${result.similarityScore >= 70 ? 'bg-green-500' : result.similarityScore >= 40 ? 'bg-yellow-500' : 'bg-red-500'}`}
                                  style={{ width: `${result.similarityScore}%` }}
                                ></div>
                              </div>
                              <span className="text-sm font-semibold">{result.similarityScore}%</span>
                            </div>
                          </div>
                        )}
                      </div>
                    </div>
                  )}
                </div>
              ))}
            </div>
          </div>
        )}

        {/* Instructions */}
        <div className="bg-blue-50 p-4 rounded mt-8">
          <h3 className="font-semibold mb-2">How to Use:</h3>
          <ol className="text-sm space-y-1 list-decimal list-inside">
            <li>Choose input method: manual entry or file upload</li>
            <li>For manual entry: Enter URLs separated by | for title and summary (optional)</li>
            <li>For files: Upload CSV (with url, title, summary columns), JSON array, or plain text with URLs</li>
            <li>Click "Start Analysis" to process all URLs</li>
            <li>View results with metadata, generated summaries, and similarity scores</li>
            <li>Export results as JSON or CSV for further analysis</li>
          </ol>
          <p className="text-xs text-gray-600 mt-2">
            Note: This tool uses Claude API for metadata extraction and summary generation. Processing may take time for large datasets.
          </p>
        </div>
      </div>
    </div>
  );
};

