import { Upload, FileText, Globe, Download, Play, Pause, AlertCircle, CheckCircle, Eye, EyeOff } from 'lucide-react';

const URLMetadataAnalyzer = () => {
  const [inputData, setInputData] = useState([]);
  const [results, setResults] = useState([]);
  const [isProcessing, setIsProcessing] = useState(false);
  const [processingIndex, setProcessingIndex] = useState(-1);
  const [inputMethod, setInputMethod] = useState('manual');
  const [textInput, setTextInput] = useState('');
  const [htmlInput, setHtmlInput] = useState('');
  const [showApiKey, setShowApiKey] = useState(false);
  const fileInputRef = useRef(null);

  // Sample data structure
  const sampleData = [
    {
      url: 'https://towardsdatascience.com/the-mcp-security-survival-guide-best-practices-pitfalls-and-real-world-lessons/',
      title: 'The MCP Security Survival Guide',
      summary: 'Comprehensive guide covering MCP security best practices, real-world vulnerabilities, and lessons learned from security breaches'
    },
    {
      url: 'https://www.anthropic.com',
      title: 'Anthropic Homepage',
      summary: 'AI safety company focused on building safe, beneficial AI systems'
    },
    {
      url: 'https://docs.anthropic.com',
      title: 'Anthropic Documentation', 
      summary: 'Technical documentation for Claude API and AI safety research'
    }
  ];

  const validateUrl = (urlString) => {
    try {
      const url = new URL(urlString);
      return url.protocol === 'http:' || url.protocol === 'https:';
    } catch {
      return false;
    }
  };

  const extractMetadataFromHTML = (html, url) => {
    try {
      // Create a temporary DOM element to parse HTML
      const parser = new DOMParser();
      const doc = parser.parseFromString(html, 'text/html');

      // Helper function to get meta content by various attributes
      const getMetaContent = (selectors) => {
        for (const selector of selectors) {
          const meta = doc.querySelector(selector);
          if (meta) {
            return meta.getAttribute('content') || meta.getAttribute('datetime') || meta.textContent?.trim() || '';
          }
        }
        return '';
      };

      // Extract title
      const title = doc.title || 
                   getMetaContent(['meta[property="og:title"]', 'meta[name="twitter:title"]']) || 
                   'No title found';

      // Extract description
      const description = getMetaContent([
        'meta[name="description"]',
        'meta[property="og:description"]', 
        'meta[name="twitter:description"]'
      ]) || doc.querySelector('p')?.textContent?.trim()?.substring(0, 200) || 'No description found';

      // Extract author
      const author = getMetaContent([
        'meta[name="author"]',
        'meta[property="article:author"]',
        'meta[name="twitter:creator"]',
        '[rel="author"]'
      ]) || (() => {
        // Look for author in common patterns
        const authorPatterns = [
          '.author', '.byline', '.post-author', '.article-author',
          '[class*="author"]', '[class*="byline"]'
        ];
        for (const pattern of authorPatterns) {
          const element = doc.querySelector(pattern);
          if (element?.textContent?.trim()) {
            return element.textContent.trim();
          }
        }
        return 'Unknown';
      })();

      // Extract publication date
      const publishDate = (() => {
        const dateSelectors = [
          'meta[property="article:published_time"]',
          'meta[name="date"]',
          'meta[name="publish_date"]',
          'time[datetime]',
          'time[pubdate]',
          '.published-date',
          '.date',
          '.post-date',
          '.article-date',
          '[datetime]'
        ];

        return getMetaContent(dateSelectors) || 'Unknown';
      })();

      // Extract keywords
      const keywords = getMetaContent([
        'meta[name="keywords"]',
        'meta[property="article:tag"]'
      ]) || 'Unknown';

      // Extract Open Graph image
      const ogImage = getMetaContent([
        'meta[property="og:image"]',
        'meta[name="twitter:image"]'
      ]) || '';

      // Extract text content for analysis
      const textContent = doc.body?.textContent || '';
      const words = textContent.trim().split(/\s+/).filter(word => word.length > 0);
      const wordCount = words.length;

      // Extract paragraphs
      const paragraphs = Array.from(doc.querySelectorAll('p'))
        .map(p => p.textContent?.trim())
        .filter(p => p && p.length > 50);

      const firstParagraph = paragraphs[0]?.substring(0, 300) || 'No content found';

      // Detect content type
      const contentType = (() => {
        const ogType = getMetaContent(['meta[property="og:type"]']);
        if (ogType) return ogType;

        if (url.includes('blog') || paragraphs.length > 3) return 'article';
        if (url.includes('product') || url.includes('shop')) return 'product';
        if (url === new URL(url).origin || url.endsWith('/')) return 'homepage';
        return 'webpage';
      })();

      // Detect language
      const language = doc.documentElement?.lang || 
                      getMetaContent(['meta[name="language"]', 'meta[http-equiv="content-language"]']) || 
                      'en';

      // Get canonical URL
      const canonicalUrl = doc.querySelector('link[rel="canonical"]')?.href || url;

      return {
        title: title.substring(0, 200),
        description: description.substring(0, 500),
        domain: new URL(url).hostname,
        wordCount: wordCount,
        contentType: contentType,
        language: language,
        publishDate: publishDate,
        scanDate: new Date().toISOString(),
        author: author.substring(0, 100),
        keywords: keywords.substring(0, 200),
        canonicalUrl: canonicalUrl,
        ogImage: ogImage,
        firstParagraph: firstParagraph,
        paragraphCount: paragraphs.length,
        charset: doc.characterSet || 'Unknown'
      };
    } catch (error) {
      console.error('Error parsing HTML:', error);
      return {
        title: "HTML parsing error",
        description: error.message,
        domain: new URL(url).hostname,
        wordCount: 0,
        contentType: "Error",
        language: "Unknown",
        publishDate: "Unknown",
        scanDate: new Date().toISOString(),
        author: "Unknown",
        keywords: "Unknown",
        canonicalUrl: url,
        ogImage: "",
        firstParagraph: "Error parsing content",
        paragraphCount: 0,
        charset: "Unknown"
      };
    }
  };

  const fetchWebpageContent = async (url) => {
    try {
      // Method 1: Try using a CORS proxy
      const proxyUrls = [
        `https://api.allorigins.win/raw?url=${encodeURIComponent(url)}`,
        `https://thingproxy.freeboard.io/fetch/${url}`
      ];

      for (const proxyUrl of proxyUrls) {
        try {
          const response = await fetch(proxyUrl, {
            headers: {
              'User-Agent': 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36'
            }
          });

          if (response.ok) {
            const html = await response.text();
            if (html && html.length > 100) { // Basic check for valid HTML
              return html;
            }
          }
        } catch (proxyError) {
          console.log(`Proxy ${proxyUrl} failed:`, proxyError.message);
          continue;
        }
      }

      // Method 2: Try direct fetch (will likely fail due to CORS, but worth trying)
      try {
        const response = await fetch(url, {
          mode: 'cors',
          headers: {
            'User-Agent': 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36'
          }
        });

        if (response.ok) {
          return await response.text();
        }
      } catch (corsError) {
        console.log('Direct fetch failed due to CORS:', corsError.message);
      }

      // Method 3: Use Claude to analyze the URL (fallback)
      console.log('Falling back to Claude analysis...');
      const response = await fetch("https://api.anthropic.com/v1/messages", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          model: "claude-sonnet-4-20250514",
          max_tokens: 2000,
          messages: [
            {
              role: "user",
              content: `I need you to fetch and analyze this webpage: ${url}

              Please extract comprehensive metadata and return it as JSON:
              {
                "title": "exact page title from <title> tag",
                "description": "meta description or main content excerpt",
                "domain": "domain name",
                "wordCount": "estimated word count of main content",
                "contentType": "article/blog/homepage/product/etc",
                "language": "detected language code",
                "publishDate": "publication date if available (ISO format)",
                "author": "author name if available",
                "keywords": "meta keywords or main topics",
                "ogImage": "open graph image URL if available",
                "firstParagraph": "first meaningful paragraph of content",
                "paragraphCount": "estimated number of paragraphs",
                "charset": "character encoding"
              }

              Be thorough in extracting this information. If something isn't available, use "Unknown" or null.
              Return ONLY valid JSON, no other text or formatting.`
            }
          ]
        })
      });

      if (response.ok) {
        const data = await response.json();
        const content = data.content[0].text;

        try {
          // Clean up the response and parse JSON
          const cleanedContent = content.replace(/```json\n?/g, "").replace(/```\n?/g, "").trim();
          const metadata = JSON.parse(cleanedContent);

          // Add scan date and domain if missing
          metadata.scanDate = new Date().toISOString();
          if (!metadata.domain) {
            metadata.domain = new URL(url).hostname;
          }

          // Return metadata object that can be used by extractMetadataFromHTML format
          return {
            isClaudeAnalysis: true,
            metadata: metadata
          };
        } catch (parseError) {
          throw new Error('Could not parse Claude analysis response');
        }
      } else {
        throw new Error(`Claude API error: ${response.status}`);
      }

    } catch (error) {
      console.error('All fetch methods failed:', error);
      throw new Error(`Unable to fetch webpage content: ${error.message}`);
    }
  };

  const extractMetadata = async (url) => {
    try {
      // Try to fetch webpage content
      const result = await fetchWebpageContent(url);

      // If we got a Claude analysis result, return it directly
      if (result && result.isClaudeAnalysis) {
        return result.metadata;
      }

      // If we got HTML content, parse it
      if (typeof result === 'string') {
        return extractMetadataFromHTML(result, url);
      }

      throw new Error('No content retrieved');

    } catch (error) {
      console.error('Error extracting metadata:', error);

      // Return basic info we can determine from URL as fallback
      return {
        title: "Unable to fetch page",
        description: `Could not access webpage: ${error.message}`,
        domain: new URL(url).hostname,
        wordCount: 0,
        contentType: "Unknown",
        language: "Unknown",
        publishDate: "Unknown",
        scanDate: new Date().toISOString(),
        author: "Unknown",
        keywords: "Unknown",
        canonicalUrl: url,
        ogImage: "",
        firstParagraph: "Could not fetch content",
        paragraphCount: 0,
        charset: "Unknown"
      };
    }
  };

  const generateSummary = async (url, metadata) => {
    try {
      const response = await fetch("https://api.anthropic.com/v1/messages", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          model: "claude-sonnet-4-20250514",
          max_tokens: 300,
          messages: [
            {
              role: "user",
              content: `Based on the URL ${url} and this metadata: ${JSON.stringify(metadata)}, 
              generate a concise 2-3 sentence summary of what this webpage is about. 
              Focus on the main purpose, content type, and target audience.`
            }
          ]
        })
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const data = await response.json();
      return data.content[0].text.trim();
    } catch (error) {
      console.error('Error generating summary:', error);
      return "Unable to generate summary due to API error.";
    }
  };

  const calculateSimilarity = async (summary1, summary2) => {
    try {
      const response = await fetch("https://api.anthropic.com/v1/messages", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          model: "claude-sonnet-4-20250514",
          max_tokens: 100,
          messages: [
            {
              role: "user",
              content: `Compare these two summaries and give a similarity score from 0-100:

              Summary 1: "${summary1}"
              Summary 2: "${summary2}"

              Return only a number between 0-100 representing similarity percentage.`
            }
          ]
        })
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const data = await response.json();
      const score = parseInt(data.content[0].text.trim());
      return isNaN(score) ? 0 : Math.min(100, Math.max(0, score));
    } catch (error) {
      console.error('Error calculating similarity:', error);
      return 0;
    }
  };

  const processUrls = async () => {
    if (inputData.length === 0) {
      alert('Please add some URLs to process');
      return;
    }

    setIsProcessing(true);
    setResults([]);

    const newResults = [];

    for (let i = 0; i < inputData.length; i++) {
      setProcessingIndex(i);
      const item = inputData[i];

      const result = {
        originalData: item,
        isValid: validateUrl(item.url),
        metadata: null,
        generatedSummary: '',
        similarityScore: null,
        error: null
      };

      // Check if this is HTML analysis with pre-extracted metadata
      if (item.isHtmlAnalysis && item.htmlMetadata) {
        result.metadata = item.htmlMetadata;
        result.isValid = true;

        // Generate summary based on the extracted content
        try {
          result.generatedSummary = await generateSummary(item.url, result.metadata);

          // Calculate similarity if original summary provided
          if (item.summary && item.summary.trim()) {
            result.similarityScore = await calculateSimilarity(item.summary, result.generatedSummary);
          }
        } catch (error) {
          result.generatedSummary = result.metadata.description || result.metadata.firstParagraph || 'Content extracted from HTML';
          result.error = 'Could not generate AI summary: ' + error.message;
        }
      } else if (result.isValid) {
        try {
          // Extract metadata
          result.metadata = await extractMetadata(item.url);

          // Generate summary
          result.generatedSummary = await generateSummary(item.url, result.metadata);

          // Calculate similarity if original summary provided
          if (item.summary && item.summary.trim()) {
            result.similarityScore = await calculateSimilarity(item.summary, result.generatedSummary);
          }
        } catch (error) {
          result.error = error.message;
        }
      } else {
        result.error = 'Invalid URL format';
      }

      newResults.push(result);
      setResults([...newResults]);

      // Small delay to prevent overwhelming the API
      if (i < inputData.length - 1) {
        await new Promise(resolve => setTimeout(resolve, 1000));
      }
    }

    setIsProcessing(false);
    setProcessingIndex(-1);
  };

  const handleFileUpload = async (event) => {
    const file = event.target.files[0];
    if (!file) return;

    const text = await file.text();

    try {
      if (file.name.endsWith('.json')) {
        const jsonData = JSON.parse(text);
        const urls = Array.isArray(jsonData) ? jsonData : [jsonData];
        setInputData(urls.map(item => ({
          url: item.url || item.link || item.href || '',
          title: item.title || '',
          summary: item.summary || item.description || ''
        })));
      } else if (file.name.endsWith('.csv')) {
        const lines = text.split('\n').filter(line => line.trim());
        const headers = lines[0].split(',').map(h => h.trim().toLowerCase());
        const data = [];

        for (let i = 1; i < lines.length; i++) {
          const values = lines[i].split(',');
          const item = {};
          headers.forEach((header, index) => {
            const value = values[index]?.trim() || '';
            if (header.includes('url') || header.includes('link')) {
              item.url = value;
            } else if (header.includes('title') || header.includes('name')) {
              item.title = value;
            } else if (header.includes('summary') || header.includes('description')) {
              item.summary = value;
            }
          });
          if (item.url) data.push(item);
        }
        setInputData(data);
      } else {
        // Treat as plain text with URLs
        const urls = text.split('\n')
          .filter(line => line.trim())
          .map(url => ({ url: url.trim(), title: '', summary: '' }));
        setInputData(urls);
      }
    } catch (error) {
      alert('Error parsing file: ' + error.message);
    }
  };

  const handleHtmlInput = () => {
    const lines = htmlInput.trim().split('\n');

    if (lines.length >= 2 && lines[0].startsWith('http')) {
      // Format: URL on first line, HTML on subsequent lines
      const url = lines[0];
      const html = lines.slice(1).join('\n');

      try {
        const metadata = extractMetadataFromHTML(html, url);
        setInputData([{
          url: url,
          title: metadata.title,
          summary: metadata.description,
          isHtmlAnalysis: true,
          htmlMetadata: metadata
        }]);
      } catch (error) {
        alert('Error parsing HTML: ' + error.message);
      }
    } else {
      // Assume it's just HTML, use a placeholder URL
      const url = 'https://example.com';
      try {
        const metadata = extractMetadataFromHTML(htmlInput, url);
        setInputData([{
          url: url,
          title: metadata.title,
          summary: metadata.description,
          isHtmlAnalysis: true,
          htmlMetadata: metadata
        }]);
      } catch (error) {
        alert('Error parsing HTML: ' + error.message);
      }
    }

    setHtmlInput('');
  };

  const handleTextInput = () => {
    const lines = textInput.split('\n').filter(line => line.trim());
    const urls = lines.map(line => {
      const parts = line.split('|').map(p => p.trim());
      return {
        url: parts[0] || '',
        title: parts[1] || '',
        summary: parts[2] || ''
      };
    });
    setInputData(urls);
    setTextInput('');
  };

  const exportResults = (format) => {
    if (results.length === 0) {
      alert('No results to export');
      return;
    }

    let content, filename, mimeType;

    if (format === 'json') {
      content = JSON.stringify(results, null, 2);
      filename = 'url-analysis-results.json';
      mimeType = 'application/json';
    } else if (format === 'csv') {
      const headers = ['URL', 'Original Title', 'Original Summary', 'Is Valid', 'Extracted Title', 'Description', 'Domain', 'Content Type', 'Word Count', 'Language', 'Author', 'Publish Date', 'Scan Date', 'Keywords', 'Generated Summary', 'Similarity Score', 'Error'];
      const rows = results.map(result => [
        result.originalData.url,
        result.originalData.title,
        result.originalData.summary,
        result.isValid,
        result.metadata?.title || '',
        result.metadata?.description || '',
        result.metadata?.domain || '',
        result.metadata?.contentType || '',
        result.metadata?.wordCount || '',
        result.metadata?.language || '',
        result.metadata?.author || '',
        result.metadata?.publishDate || '',
        result.metadata?.scanDate || '',
        result.metadata?.keywords || '',
        result.generatedSummary,
        result.similarityScore || '',
        result.error || ''
      ]);
      content = [headers, ...rows].map(row => row.map(cell => `"${cell}"`).join(',')).join('\n');
      filename = 'url-analysis-results.csv';
      mimeType = 'text/csv';
    }

    const blob = new Blob([content], { type: mimeType });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = filename;
    a.click();
    URL.revokeObjectURL(url);
  };

  const loadSampleData = () => {
    setInputData(sampleData);
  };

  return (
    <div className="max-w-6xl mx-auto p-6 bg-gray-50 min-h-screen">
      <div className="bg-white rounded-lg shadow-lg p-6">
        <div className="text-center mb-8">
          <h1 className="text-3xl font-bold text-gray-800 mb-2">URL Metadata Analyzer</h1>
          <p className="text-gray-600">Validate URLs, extract metadata, generate summaries, and compare content</p>
        </div>

        {/* CORS Notice */}
        <div className="bg-yellow-50 border border-yellow-200 p-4 rounded mb-4">
          <div className="flex items-start">
            <AlertCircle className="text-yellow-600 mr-2 mt-0.5" size={16} />
            <div className="text-sm">
              <p className="font-semibold text-yellow-800 mb-1">Web Fetching Limitations</p>
              <p className="text-yellow-700">
                Due to browser security (CORS), direct webpage fetching may not work for all sites. 
                For best results with the <strong>Towards Data Science</strong> URL:
              </p>
              <ol className="list-decimal list-inside mt-2 text-yellow-700">
                <li>Try the <strong>Manual Entry</strong> method first (it will attempt multiple fetch strategies)</li>
                <li>Or use <strong>HTML Analysis</strong>: visit the page, view source (Ctrl+U), and paste the HTML</li>
                <li>The app will extract comprehensive metadata from the HTML content</li>
              </ol>
            </div>
          </div>
        </div>

        {/* Input Section */}
        <div className="mb-8">
          <h2 className="text-xl font-semibold mb-4 flex items-center">
            <Upload className="mr-2" size={20} />
            Input Data
          </h2>

          <div className="mb-4">
            <div className="flex gap-4 mb-4">
              <button
                onClick={() => setInputMethod('manual')}
                className={`px-4 py-2 rounded ${inputMethod === 'manual' ? 'bg-blue-500 text-white' : 'bg-gray-200'}`}
              >
                Manual Entry
              </button>
              <button
                onClick={() => setInputMethod('file')}
                className={`px-4 py-2 rounded ${inputMethod === 'file' ? 'bg-blue-500 text-white' : 'bg-gray-200'}`}
              >
                File Upload
              </button>
              <button
                onClick={() => setInputMethod('html')}
                className={`px-4 py-2 rounded ${inputMethod === 'html' ? 'bg-blue-500 text-white' : 'bg-gray-200'}`}
              >
                HTML Analysis
              </button>
              <button
                onClick={loadSampleData}
                className="px-4 py-2 bg-green-500 text-white rounded hover:bg-green-600"
              >
                Load Sample Data
              </button>
            </div>

            {inputMethod === 'manual' && (
              <div>
                <textarea
                  value={textInput}
                  onChange={(e) => setTextInput(e.target.value)}
                  placeholder="Enter URLs, one per line. Use | to separate URL|Title|Summary&#10;Example:&#10;https://example.com|Example Site|A sample website&#10;https://another.com|Another Site"
                  className="w-full h-32 p-3 border rounded resize-none"
                />
                <button
                  onClick={handleTextInput}
                  className="mt-2 px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600"
                >
                  Add URLs
                </button>
              </div>
            )}

            {inputMethod === 'html' && (
              <div>
                <div className="mb-2">
                  <label className="block text-sm font-medium mb-1">Paste HTML Content for Analysis</label>
                  <textarea
                    value={htmlInput}
                    onChange={(e) => setHtmlInput(e.target.value)}
                    placeholder="Paste the HTML content of a webpage here to test metadata extraction...&#10;&#10;Or enter a URL and HTML on separate lines:&#10;https://example.com&#10;<html><head><title>Example</title>..."
                    className="w-full h-32 p-3 border rounded resize-none font-mono text-sm"
                  />
                </div>
                <button
                  onClick={handleHtmlInput}
                  className="px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600"
                >
                  Analyze HTML
                </button>
                <p className="text-sm text-gray-500 mt-2">
                  Tip: You can view page source (Ctrl+U) on any webpage and paste the HTML here to test extraction
                </p>
              </div>
            )}

            {inputMethod === 'file' && (
              <div>
                <input
                  ref={fileInputRef}
                  type="file"
                  accept=".csv,.json,.txt"
                  onChange={handleFileUpload}
                  className="hidden"
                />
                <button
                  onClick={() => fileInputRef.current?.click()}
                  className="px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600 flex items-center"
                >
                  <FileText className="mr-2" size={16} />
                  Choose File (CSV, JSON, TXT)
                </button>
                <p className="text-sm text-gray-500 mt-2">
                  CSV: headers should include url/link, title, summary/description<br/>
                  JSON: array of objects with url, title, summary fields<br/>
                  TXT: one URL per line
                </p>
              </div>
            )}
          </div>

          {/* Input Data Preview */}
          {inputData.length > 0 && (
            <div className="mt-4">
              <h3 className="font-semibold mb-2">Input Data ({inputData.length} URLs)</h3>
              <div className="max-h-40 overflow-y-auto border rounded p-2 bg-gray-50">
                {inputData.map((item, index) => (
                  <div key={index} className="text-sm mb-1">
                    <span className="font-mono text-blue-600">{item.url}</span>
                    {item.title && <span className="ml-2 text-gray-600">• {item.title}</span>}
                  </div>
                ))}
              </div>
            </div>
          )}
        </div>

        {/* Processing Section */}
        <div className="mb-8">
          <div className="flex items-center justify-between">
            <h2 className="text-xl font-semibold flex items-center">
              <Globe className="mr-2" size={20} />
              Process URLs
            </h2>
            <button
              onClick={processUrls}
              disabled={isProcessing || inputData.length === 0}
              className="px-6 py-2 bg-green-500 text-white rounded hover:bg-green-600 disabled:opacity-50 flex items-center"
            >
              {isProcessing ? (
                <>
                  <Pause className="mr-2" size={16} />
                  Processing...
                </>
              ) : (
                <>
                  <Play className="mr-2" size={16} />
                  Start Analysis
                </>
              )}
            </button>
          </div>

          {isProcessing && (
            <div className="mt-4">
              <div className="bg-blue-50 p-4 rounded">
                <p className="text-sm text-blue-700">
                  Processing URL {processingIndex + 1} of {inputData.length}...
                </p>
                <div className="w-full bg-blue-200 rounded-full h-2 mt-2">
                  <div 
                    className="bg-blue-500 h-2 rounded-full transition-all duration-300"
                    style={{ width: `${((processingIndex + 1) / inputData.length) * 100}%` }}
                  ></div>
                </div>
              </div>
            </div>
          )}
        </div>

        {/* Results Section */}
        {results.length > 0 && (
          <div className="mb-8">
            <div className="flex items-center justify-between mb-4">
              <h2 className="text-xl font-semibold">Results ({results.length})</h2>
              <div className="flex gap-2">
                <button
                  onClick={() => exportResults('json')}
                  className="px-4 py-2 bg-purple-500 text-white rounded hover:bg-purple-600 flex items-center"
                >
                  <Download className="mr-2" size={16} />
                  Export JSON
                </button>
                <button
                  onClick={() => exportResults('csv')}
                  className="px-4 py-2 bg-purple-500 text-white rounded hover:bg-purple-600 flex items-center"
                >
                  <Download className="mr-2" size={16} />
                  Export CSV
                </button>
              </div>
            </div>

            <div className="space-y-4 max-h-96 overflow-y-auto">
              {results.map((result, index) => (
                <div key={index} className="border rounded p-4 bg-white">
                  <div className="flex items-start justify-between mb-2">
                    <div className="flex-1">
                      <div className="flex items-center mb-1">
                        {result.isValid ? (
                          <CheckCircle className="text-green-500 mr-2" size={16} />
                        ) : (
                          <AlertCircle className="text-red-500 mr-2" size={16} />
                        )}
                        <span className="font-mono text-sm text-blue-600 break-all">
                          {result.originalData.url}
                        </span>
                      </div>
                      {result.originalData.title && (
                        <p className="text-sm text-gray-600 mb-1">
                          <strong>Original Title:</strong> {result.originalData.title}
                        </p>
                      )}
                    </div>
                  </div>

                  {result.error ? (
                    <div className="text-red-600 text-sm">
                      <strong>Error:</strong> {result.error}
                    </div>
                  ) : result.metadata && (
                    <div className="grid md:grid-cols-2 gap-4 text-sm">
                      <div>
                        <h4 className="font-semibold mb-2">Extracted Metadata</h4>
                        <p><strong>Title:</strong> {result.metadata.title}</p>
                        <p><strong>Domain:</strong> {result.metadata.domain}</p>
                        <p><strong>Content Type:</strong> {result.metadata.contentType}</p>
                        <p><strong>Language:</strong> {result.metadata.language}</p>
                        <p><strong>Word Count:</strong> {result.metadata.wordCount}</p>
                        {result.metadata.author && result.metadata.author !== 'Unknown' && (
                          <p><strong>Author:</strong> {result.metadata.author}</p>
                        )}
                        {result.metadata.publishDate && result.metadata.publishDate !== 'Unknown' && (
                          <p><strong>Published:</strong> {new Date(result.metadata.publishDate).toLocaleDateString()}</p>
                        )}
                        <p><strong>Scanned:</strong> {new Date(result.metadata.scanDate).toLocaleDateString()}</p>
                        {result.metadata.keywords && result.metadata.keywords !== 'Unknown' && (
                          <p><strong>Keywords:</strong> {result.metadata.keywords.substring(0, 100)}</p>
                        )}
                        {result.metadata.description && (
                          <p><strong>Description:</strong> {result.metadata.description.substring(0, 150)}...</p>
                        )}
                        {result.metadata.firstParagraph && result.metadata.firstParagraph !== 'Could not extract content' && (
                          <p><strong>First Paragraph:</strong> {result.metadata.firstParagraph.substring(0, 200)}...</p>
                        )}
                      </div>
                      <div>
                        <h4 className="font-semibold mb-2">Generated Summary</h4>
                        <p className="text-gray-700 mb-2">{result.generatedSummary}</p>

                        {result.originalData.summary && result.similarityScore !== null && (
                          <div className="mt-2">
                            <h5 className="font-semibold">Summary Comparison</h5>
                            <p><strong>Original:</strong> {result.originalData.summary}</p>
                            <div className="flex items-center mt-1">
                              <span className="mr-2">Similarity Score:</span>
                              <div className="flex-1 bg-gray-200 rounded-full h-2 mr-2">
                                <div 
                                  className={`h-2 rounded-full ${result.similarityScore >= 70 ? 'bg-green-500' : result.similarityScore >= 40 ? 'bg-yellow-500' : 'bg-red-500'}`}
                                  style={{ width: `${result.similarityScore}%` }}
                                ></div>
                              </div>
                              <span className="text-sm font-semibold">{result.similarityScore}%</span>
                            </div>
                          </div>
                        )}
                      </div>
                    </div>
                  )}
                </div>
              ))}
            </div>
          </div>
        )}

        {/* Instructions */}
        <div className="bg-blue-50 p-4 rounded mt-8">
          <h3 className="font-semibold mb-2">How to Use:</h3>
          <ol className="text-sm space-y-1 list-decimal list-inside">
            <li>Choose input method: manual entry, file upload, or HTML analysis</li>
            <li>For manual entry: Enter URLs separated by | for title and summary (optional)</li>
            <li>For HTML analysis: Paste HTML source code for guaranteed metadata extraction</li>
            <li>For files: Upload CSV (with url, title, summary columns), JSON array, or plain text with URLs</li>
            <li>Click "Start Analysis" to process all URLs</li>
            <li>View results with metadata, generated summaries, and similarity scores</li>
            <li>Export results as JSON or CSV for further analysis</li>
          </ol>
          <p className="text-xs text-gray-600 mt-2">
            Note: This tool uses Claude API for summary generation and similarity analysis. For best metadata extraction, use the HTML Analysis method.
          </p>
        </div>
      </div>
    </div>
  );
};

